import React, { ChangeEvent, SyntheticEvent, useState } from 'react';
import './App.css';
import CardList from './Components/CardList/CardList';
import Search from './Components/Search/Search';
import { CompanySearch } from './company';
import { searchCompanies } from './api';

const App: React.FC = () => {
  const [search, setSearch] = useState<string>('');
  const [searchResult, setSearchResult] = useState<CompanySearch[]>([]);
  const [serverError, setServerError] = useState<string>('');

  /**
   * Updates the search state with the input value.
   * @param e - ChangeEvent from the input element.
   */
  const handleChange = (e: ChangeEvent<HTMLInputElement>): void => {
    setSearch(e.target.value);
  };

  /**
   * Handles the click event and performs a company search.
   * @param e - SyntheticEvent from the button element.
   */
  const onClick = async (e: SyntheticEvent): Promise<void> => {
    const result = await searchCompanies(search);
    if (typeof result === 'string') {
      setServerError(result);
    } else {
      if (Array.isArray(result.data)) {
        setSearchResult(result.data);
      }
      console.logP
    }
  };

  return (
    <div className="App">
      <Search onClick={onClick} search={search} handleChange={handleChange} />
      <CardList />
    </div>
  );
};

export default App;
