import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router'
import { CompanyProfile } from '../../company';
import { getCompanyProfile } from '../../api';

interface Props {}

const CompanyPage = (props: Props) => {
  let { ticker } = useParams<{ ticker: string }>();
  const [company, setCompany] = useState<CompanyProfile | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchCompany = async () => {
      try {
        if (ticker) {
          const result = await getCompanyProfile(ticker);
          setCompany(result);
          setError(null);
        }
      } catch (err) {
        setError(err instanceof Error ? err.message : "Unknown error occurred");
        setCompany(null);
      }
    };
    fetchCompany();
  }, [ticker])

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
    {company ? (
      <div>
        <h1>{company.companyName}</h1>
        <p>Symbol: {company.symbol}</p>
        <p>Price: ${company.price.toFixed(2)}</p>
        <p>Market Cap: ${company.marketCap.toLocaleString()}</p>
        <p>CEO: {company.ceo}</p>
        <p>Industry: {company.industry}</p>
        <p>Website: <a href={company.website} target="_blank" rel="noopener noreferrer">{company.website}</a></p>
      </div>
    ) : (
      <div>Loading company data...</div>
    )}
  </div>
  )
}

export default CompanyPage
