import React from 'react'

type Props = {
  data: Company[]
}

type Company = {
  cik: string
  year: number
  income: number
}

type TableConfig = {
  label: string
  render: (company: Company) => React.ReactNode
}

const configs: TableConfig[] = [
  {
    label: "Год",
    render: (company) => company.year
  },
  {
    label: "Доход",
    render: (company) => company.income
  }
]

const Table = ({ data }: Props) => {
  const renderedRows = data.map((company) => (
    <tr key={company.cik}>
        {configs.map((config, index) => (
        <td 
          key={`${company.cik}-${index}`}
          className='p-4 whitespace-nowrap text-sm font-normal text-gray-900'
        >
          {config.render(company)}
        </td>
      ))}
    </tr>
  ))

  const renderedHeaders = configs.map((config: any) => (
    <th 
      className='p-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider'
      key={config.label}
    >
      {config.label}
    </th>
  ))

  return (
    <div className="bg-white shadow rounded-lg p-4 sm:p-6 xl:p-8 overflow-x-auto">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            {renderedHeaders}
          </tr>
        </thead>
        <tbody className="divide-y divide-gray-200">
          {renderedRows}
        </tbody>
      </table>
    </div>
  )
}

export default Table
